    let result = [];

    // 循环遍历 arr1 的所有元素
    for (let i = 0; i < arr1.length; i++) {
        // 循环遍历 arr2 的所有元素
        for (let j = 0; j < arr2; j++){
            // 检查 arr1 和 arr2 的元素是否匹配
            if (arr1[i] === arr2[j]) {
                // 如果匹配，则将元素推送到结果数组中
