def LCS(s1, s2): 
    m = len(s1) 
    n = len(s2)

    # 用于存储dp值的二维数组 
    L = [[None]*(n + 1) for _ in range(m + 1)] 

    """下面的步骤逐步构建 L[m+1][n+1] 
    注意：L[i][j] 包含 X[0..i-1] 和 Y[0..j-1] 的LCS长度"""
    for i in range(m + 1): 
        # 第一行全部是0，没有公共序列
        for j in range(n + 1): 
            # 每一行的第一列都是0 
            if i == 0 or j == 0 : 
                L[i][j] = 0
            elif s1[i-1] == s2[j-1]: 
                L[i][j] = L[i-1][j-1]+1
            else: 
                L[i][j] = max(L[i-1][j] , L[i][j-1]) 

    # L[m][n] 包含 X[0..n-1] 和 Y[0..m-1] 的LCS长度 
    return L[m][n]

